stage_1.sql_query:
SELECT 
    (SELECT EmployeeSum FROM LC_Staff WHERE CompanyCode = 25 AND EndDate LIKE '2021-12-31%' AND ClassfiedMethod = 9000) AS TotalEmployees,
    ROUND((SELECT RatioInSum FROM LC_Staff WHERE CompanyCode = 25 AND EndDate LIKE '2021-12-31%' AND ClassfiedMethod = 3000 AND TypeCode = 3035), 2) AS TechnicalPersonnelRatio;
